  //   // reset lower flag and led
  // }

  // lower weight check
  if (lower_weight_value > weight && !lack_event_flag && weight > 0) {
    if (strncmp(weight_raw->states, "ST", 2) == 0) {
      lv_led_on(ui_led3);
      lack_volume();
      lack_event_flag = true;
      lv_led_off(ui_led1);
      lv_led_off(ui_led2);
    }
  }
  // else {
  //   over_event_flag=false;
